import React, { useState, useEffect } from 'react';
import axios from 'axios';

const SelectFeuilleMatch = ({ changerComponentAffiche, idMatch }) => {
  const [joueurs, setJoueurs] = useState([]);
  const [selectedJoueurs, setSelectedJoueurs] = useState([]);
  const [etatJoueurs, setEtatJoueurs] = useState([]);
  const [erreur, setErreur] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5000/joueur/')
      .then(response => {
        setJoueurs(response.data.joueur);
      })
      .catch(error => {
        console.log(error);
      });

      setEtatJoueurs(joueurs.map(joueur => joueur._id));
  }, []);

  const handleChangeEtat = (e, joueurId) => {
    if(e.target.value  === "remplacant") {
        console.log("oui c remp");
        setEtatJoueurs(etatJoueurs.filter(id => id !== joueurId));
    } else {
        console.log("non c tit");
        if(!etatJoueurs.find(id => id === joueurId)) {
            console.log("pas trouvé dans liste !");
            setEtatJoueurs([...etatJoueurs, joueurId])
        }
    }
  }

  const handleSelection = (e, joueurId) => {
    const isChecked = e.target.checked;
    if (isChecked) {
      setSelectedJoueurs([...selectedJoueurs, joueurId]);
      console.log(selectedJoueurs.length);
    } else {
      setSelectedJoueurs(selectedJoueurs.filter(id => id !== joueurId));
      console.log("retire joueur");
    }
  };

  const checkSeptTitulaires = () => {
    let nbTitulaires = 0;
    console.log(nbTitulaires);
    selectedJoueurs.forEach(idJoueur => {
        if (etatJoueurs.find(id => id === idJoueur)) {
            nbTitulaires++;
            console.log(nbTitulaires);
        }
    });
    console.log(nbTitulaires);
    console.log(nbTitulaires === 7);
    return nbTitulaires === 7;
};

const creerFeuilleDeMatch = (e) => {
    e.preventDefault();
    console.log("fonction est trigger");

    if (!checkSeptTitulaires()) {
        setErreur('Veuillez sélectionner 7 joueurs titulaires');
        return;
    }
    setErreur('');
};


  return (
    <article>
      <header className='containerTitre'>
        <h1>Sélection des joueurs de la feuille de match</h1>
      </header>
      <div className='containerTabAndButtons'>
        <table>
          <thead>
            <tr>
              <th>Photo</th>
              <th>Taille</th>
              <th>Poids</th>
              <th>Poste préféré</th>
              <th>Titulaire ou remplaçant</th>
              <th>Sélectionner</th>
            </tr>
          </thead>
          <tbody>
            {joueurs.map(joueur => (
              <tr key={joueur._id}>
                <td> {joueur.photo} </td>
                <td> {joueur.taille} </td>
                <td> {joueur.poids} </td>
                <td> {joueur.postePref} </td>
                <td>
                  <select onChange={(e) => handleChangeEtat(e, joueur._id)}>
                    <option value='titulaire'>Titulaire</option>
                    <option value='remplacant'>Remplaçant</option>
                  </select>
                </td>
                <td><input type='checkbox' onChange={(e) => handleSelection(e, joueur._id)} /></td>
              </tr>
            ))}
          </tbody>
        </table>
        <div>
          <p>7 Joueurs Titulaires requis pour valider</p>
          <button onClick={creerFeuilleDeMatch}>Valider</button>
          <button onClick={() => changerComponentAffiche('ListeDesMatchs')}>Annuler</button>
          <p>{erreur}</p>
        </div>
      </div>
    </article>
  );
};

export default SelectFeuilleMatch;
