import React, { useState, useEffect } from 'react';

const ListeDesMatchs = () => {
    const [listeMatchs, setListeMatchs] = useState([]);

    useEffect(() => {
        fetch("/matchs")
        .then(response => response.json())
        .then(data => {
            setListeMatchs(data);
        })
    }, []);

    return (
        <article className='centrerTableau'>
            <header>
                <h1>Liste des matchs</h1>
                {listeMatchs.length === 0 ? (
                    <p>Chargement des matchs...</p>
                ) : (
                    <ul>
                        {listeMatchs.map((match, i) => (
                            <li key={i}>
                                <p>Date et heure : {match.dateEtHeure}</p>
                                <p>Equipe adverse : {match.nomEquipeAdverse}</p>
                                <p>Lieu : {match.lieu}</p>
                                <p>Score (Domicile - Adversaire) : {match.score} - {match.scoreAdverse}</p>
                            </li>
                        ))}
                    </ul>
                )}
            </header>
            <div>
                <button>Ajouter un match</button>
                <table>
                    <tbody>
                        <tr>
                            <th>Date</th>
                            <th>Heure</th>
                            <th>Equipe adverse</th>
                            <th>Lieu</th>
                            <th>Score (Domicile - Adversaire)</th>
                        </tr>
                    </tbody>
                </table>
            </div>
        </article>
    );
};

export default ListeDesMatchs;
