import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TableauStatistiques = () => {

    const [joueurs, setJoueurs] = useState([]);
    const [nbMatchs, setNbMatchs] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:5000/joueur/')
            .then(reponseJoueur => {
                setJoueurs(reponseJoueur.data.joueur);
            })
            .catch(error => {
                console.log(error);
            });
        axios.get('http://localhost:5000/match')
            .then(respone => {
                setNbMatchs((respone.data.match).length);
            })
            .catch(error => {
                console.log(error);
            });
    }, []);


    function calculateAge(dateOfBirth) {
        const today = new Date();
        const birthDate = new Date(dateOfBirth);
        let age = today.getFullYear() - birthDate.getFullYear();
        const month = today.getMonth() - birthDate.getMonth();
        if (month < 0 || (month === 0 && today.getDate() < birthDate.getDate())) {
            age--;
        }
        return age;
    }


    return (
        <section>
            <table>
                <thead>
                    <tr>
                        <th> Joueur </th>
                        <th> Statut </th>
                        <th> Poste préféré </th>
                        <th> Nombre total de sélection en tant que titulaire </th>
                        <th> Nombre total de sélection en tant que remplacant </th>
                        <th> Moyenne des evaluations </th>
                        <th> Pourcentage de matchs gagne </th>
                    </tr>
                </thead>
                <tbody>
                    {joueurs.map(joueur => (
                        <tr key={joueur._id}>
                            <td> {joueur.nom} {joueur.prenom} </td>
                            <td> {joueur.statut} </td>
                            <td> {joueur.postePref} </td>
                            <td>
                                {(joueur) => {


                                }}
                            </td>

                            <td> </td>
                            <td> </td>
                            <td> </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </section>
    );
};

export default TableauStatistiques;
