import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ListeDesMatchs = () => {
    const [matchs, setMatchs] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:5000/match/')
            .then(response => {
                setMatchs(response.data.match);
            })
            .catch(error => {
                console.log(error);
            });
    }, []);

    return (
        <article className='centrerTableau'>
            <header>
                <h1>Liste des matchs</h1>
            </header>
            <div>
                <button>Ajouter un match</button>
                <table>
                    <tbody>
                        <tr>
                            <th>Date</th>
                            <th>Heure</th>
                            <th>Equipe adverse</th>
                            <th>Lieu</th>
                            <th>Score (Domicile - Adversaire)</th>
                        </tr>
                        {matchs.length === 0 ? (
                            <p>Erreur lors du chargement des matchs</p>
                        ) : (
                            <tr key={i}>
                                {matchs.map((match, i) => (
                                    <tr key={i}>
                                        <p>Date et heure : {match.dateEtHeure}</p>
                                        <p>Equipe adverse : {match.nomEquipeAdverse}</p>
                                        <p>Lieu : {match.lieu}</p>
                                        <p>Score (Domicile - Adversaire) : {match.score} - {match.scoreAdverse}</p>
                                    </tr>
                                ))}
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        </article>
    );
};

export default ListeDesMatchs;
