import React from 'react';

const ListeDesMatchs = () => {
    const [listeMatchs, setlisteMatchs] = useState([{}])

    useEffect(() => {
        fetch("/match").then(
        response => response.json()
        ).then(
        data => {
            setlisteMatchs(data)
        })}, [])

    return (
        <article className='centrerTableau'>
            <header>
                <h1>Liste des matchs</h1>
                {(typeof listeMatchs.matchs === 'undefined') ? (
                    <p>Chargement des matchs...</p>
                ) : (
                    listeMatchs.matchs.map((match, i) => (
                    <p key={i}> {match} </p>
                    ))
                )}
            </header>
            <div>
                <button>Ajouter un match</button>
                <table>
                    <tbody>
                        <tr>
                            <th>Date</th>
                            <th>Heure</th>
                            <th>Equipe adverse</th>
                            <th>Lieu</th>
                            <th>Score (Domicile - Adversaire)</th>
                        </tr>
                    </tbody>
                </table>
            </div>
        </article>
    );
};

export default ListeDesMatchs;
