import React, { useState, useEffect } from 'react';
import axios from 'axios';

const StatistiquesGloables = () => {

    const [joueurs, setJoueurs] = useState([]);
    const [matchs, setMatchs] = useState([]);
    const [participations, setParticipations] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:5000/joueur/')
            .then(response => {
                setJoueurs(response.data.joueur);
            })
            .catch(error => {
                console.log(error);
            });
        axios.get('http://localhost:5000/match')
            .then(respone => {
                setMatchs(respone.data.match);
            })
            .catch(error => {
                console.log(error);
            });
        axios.get('http://localhost:5000/participation')
            .then(response => {
                setParticipations(response.data.participation);
            })
            .catch(error => {
                console.log(error);
            });
    }, []);


    return (
        <section>
            <h1> Statistiques </h1>
            <h5> Pourcentage de matchs gagnes : </h5>
            <h5> Pourcentage de matchs perdus : </h5>
            <h5> Pourcentage de matchs nuls : </h5>
        </section>
    );
};

export default StatistiquesGloables;
