import React, { useState } from 'react';
import axios from 'axios';

const FormAjouterJoueur = ({ handleShowFormChange }) => {
    const [formData, setFormData] = useState({
        nom: '',
        prenom: '',
        numLicence: '',
        dateNaissance: '',
        taille: '',
        poids: '',
        postePref: '',
        statut: '',
        photo: null
    });

    const handleChange = (e) => {
        if (e.target.name === 'photo') {
            setFormData({ ...formData, [e.target.name]: e.target.files[0] });
        } else {
            setFormData({ ...formData, [e.target.name]: e.target.value });
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const data = new FormData();
        data.append('nom', formData.nom);
        data.append('prenom', formData.prenom);
        data.append('numLicence', formData.numLicence);
        data.append('dateNaissance', formData.dateNaissance);
        data.append('taille', formData.taille);
        data.append('poids', formData.poids);
        data.append('postePref', formData.postePref);
        data.append('statut', formData.statut);
        data.append('photo', formData.photo);

        axios.post('http://localhost:5000/joueur', data)
            .then((response) => {
                console.log(response.data);
            })
            .catch((error) => {
                console.log(error);
            });
    };

    return (
        <article>
            <header>
                <h1>Ajouter un joueur</h1>
            </header>
            <section>
                <form onSubmit={handleSubmit}>
                    Nom <input type="text" name="nom" onChange={handleChange} />
                    Prenom <input type="text" name="prenom" onChange={handleChange} />
                    Numero de licence <input type="text" name="numLicence" onChange={handleChange} />
                    Date de naissance <input type="date" name="dateNaissance" onChange={handleChange} />
                    Taille en cm <input type="text" name="taille" onChange={handleChange} />
                    Poids en kg <input type="text" name="poids" onChange={handleChange} />
                    Poste prefere
                    <select name="postePref" onChange={handleChange}>
                        <option value=""> -- Choisir un poste -- </option>
                        <option value="attrapeur"> Attrapeur </option>
                        <option value="poursuiveur"> Poursuiveur </option>
                        <option value="gardien"> Gardien </option>
                        <option value="batteur"> Batteur </option>
                    </select>
                    Statut
                    <select name="statut" onChange={handleChange}>
                        <option value=""> -- Choisir un statut -- </option>
                        <option value="actif"> Actif </option>
                        <option value="blesse"> Blesse </option>
                        <option value="suspendu"> Suspendu </option>
                        <option value="absent"> Absent </option>
                    </select>
                    Photo <input type="file" />
                    <input type="submit" value="Valider" />
                    <input type="button" value="Annuler" onClick={() => handleShowFormChange(false)} />
                </form>
            </section>
        </article>
    );
};

export default FormAjouterJoueur;
