import React, { useState , useEffect} from 'react';
import axios from 'axios';

const FormModifMatch = ({changerComponentAffiche, matchAModif}) => {
    const [match, setMatch] = useState([]);
    const [dateMatch, setDateMatch] = useState('');
    const [heureMatch, setHeure] = useState('');
    const [equipeAdverse, setEquipeAdverse] = useState('');
    const [lieu, setLieu] = useState('');
    const [score, setScore] = useState('');
    const [scoreAdverse, setScoreAdverse] = useState('');
    const [erreur, setErreur] = useState('');

    const modifierMatch = (e) => {
        e.preventDefault();

        axios.put('http://localhost:5000/match/' + matchAModif, match)
        .then(response => {
            console.log('Response:', response.data);
        })
        .catch(error => {
            console.error('Error:', error);
        });

        changerComponentAffiche('ListeDesMatchs');
    }

    useEffect(() => {
        axios.get('http://localhost:5000/match/' + matchAModif)
            .then(response => {
                setMatch(response.data.match);
            })
            .catch(error => {
                console.log(error);
            });

        setDateMatch(new Date(match.dateEtHeure).toDateString());
        setHeure(new Date(match.dateEtHeure).toTimeString().slice(0, 5));
        setEquipeAdverse(match.nomEquipeAdverse);
        setLieu(match.lieu);
        setScore(match.score);
        setScoreAdverse(match.scoreAdverse);
    }, []);

    return (
        <article>
            <header className='containerTitre'>
                <h1>Modifier un match</h1>
            </header>
            <div className='containerTabAndButtons'>
                <form>
                Date :{' '}
          <input
            type="date"
            name="dateMatch"
            value={dateMatch}
            onChange={(e) => setDateMatch(e.target.value)}
          />
          Heure :{' '}
          <input
            type="time"
            name="heure"
            value={heureMatch}
            onChange={(e) => setHeure(e.target.value)}
          />
          Equipe adverse :{' '}
          <input
            type="text"
            name="equipeAdverse"
            value={equipeAdverse}
            onChange={(e) => setEquipeAdverse(e.target.value)}
          />
          Lieu :{' '}
          <input
            type="text"
            name="lieu"
            value={lieu}
            onChange={(e) => setLieu(e.target.value)}
          />
                    Score de l'équipe <input type="number" name="score" />
                    Score de l'équipe adverse <input type="number" name="scoreAdverse" />
                </form>
                <div>
                    <button>Valider</button>
                    <button onClick={() => changerComponentAffiche('ListeDesMatchs')}>Annuler</button>
                </div>
            </div>
        </article>
    );
};

export default FormModifMatch;
