import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TableauStatistiques = () => {

    const [joueurs, setJoueurs] = useState([]);
    const [nbMatchs, setNbMatchs] = useState([]);
    const [nbSelections, setNbSelections] = useState({});
    const [loading, setLoading] = useState(true); // ajout de la state loading

    useEffect(() => {
        axios.get('http://localhost:5000/joueur/')
            .then(reponseJoueur => {
                setJoueurs(reponseJoueur.data.joueur);
            })
            .catch(error => {
                console.log(error);
            });

        axios.get('http://localhost:5000/match')
            .then(respone => {
                setNbMatchs((respone.data.match).length);
            })
            .catch(error => {
                console.log(error);
            });
    }, []);

    useEffect(() => {
        let loadedJoueurs = 0; // ajout du compteur loadedJoueurs
        setLoading(true);
        joueurs.forEach(joueur => {
            axios.get('http://localhost:5000/participation/joueur/' + joueur._id)
                .then(response =>
                    response.data.participation.forEach(participation => {
                        const nbSelectionTitulaire = nbSelections[joueur._id]?.nbSelectionTitulaire || 0;
                        const nbSelectionRemplacant = nbSelections[joueur._id]?.nbSelectionRemplacant || 0;

                        if (participation.poste === "titulaire") {
                            setNbSelections(prevState => ({
                                ...prevState,
                                [joueur._id]: {
                                    nbSelectionTitulaire: nbSelectionTitulaire + 1,
                                    nbSelectionRemplacant
                                }
                            }));
                        } else {
                            setNbSelections(prevState => ({
                                ...prevState,
                                [joueur._id]: {
                                    nbSelectionTitulaire,
                                    nbSelectionRemplacant: nbSelectionRemplacant + 1
                                }
                            }));
                        }
                    }))
                .finally(() => { // ajout de la vérification de l'état de la récupération des données
                    loadedJoueurs++;
                    if (loadedJoueurs === joueurs.length) {
                        setLoading(false);
                    }
                });
        });
    }, [joueurs]);

    if (loading) { // ajout de l'affichage d'une indication de chargement
        return <div>Loading...</div>
    }

    return (
        <section>
            <table>
                <thead>
                    <tr>
                        <th> Joueur </th>
                        <th> Statut </th>
                        <th> Poste préféré </th>
                        <th> Nombre total de sélection en tant que titulaire </th>
                        <th> Nombre total de sélection en tant que remplaçant </th>
                        <th> Moyenne des évaluations </th>
                        <th> Pourcentage de matchs gagnés </th>
                    </tr>
                </thead>
                <tbody>
                    {joueurs.map(joueur => (
                        <tr key={joueur._id}>
                            <td> {joueur.nom} {joueur.prenom} </td>
                            <td> {joueur.statut} </td>
                            <td> {joueur.postePref} </td>
                            <td> {nbSelections[joueur._id]?.nbSelectionTitulaire || 0} </td>
                            <td> {nbSelections[joueur._id]?.nbSelectionRemplacant || 0} </td>
                            <td> </td>
                            <td> </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </section>
    );
};

export default TableauStatistiques;
