import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TableauStatistiques = () => {

    const [joueurs, setJoueurs] = useState([]);
    const [nbMatchs, setNbMatchs] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:5000/joueur/')
            .then(reponseJoueur => {
                setJoueurs(reponseJoueur.data.joueur);
            })
            .catch(error => {
                console.log(error);
            });
        axios.get('http://localhost:5000/match')
            .then(respone => {
                setNbMatchs((respone.data.match).length);
            })
            .catch(error => {
                console.log(error);
            });
    }, []);


    function nbSelection(joueur) {
        return new Promise((resolve, reject) => {
            let nbSelectionTitulaire = 0;
            let nbSelectionRemplacant = 0;
            axios.get('http://localhost:5000/participation/joueur/' + joueur._id)
                .then(response => {
                    response.data.participation.forEach(participation => {
                        if (participation.poste === "titulaire") {
                            nbSelectionTitulaire++;
                        } else {
                            nbSelectionRemplacant++;
                        }
                    });
                    resolve([nbSelectionTitulaire, nbSelectionRemplacant]);
                })
                .catch(error => {
                    reject(error);
                });
        });
    }



    return (
        <section>
            <table>
                <thead>
                    <tr>
                        <th> Joueur </th>
                        <th> Statut </th>
                        <th> Poste préféré </th>
                        <th> Nombre total de sélection en tant que titulaire </th>
                        <th> Nombre total de sélection en tant que remplacant </th>
                        <th> Moyenne des evaluations </th>
                        <th> Pourcentage de matchs gagne </th>
                    </tr>
                </thead>
                <tbody>
                    {joueurs.map(joueur => (
                        <tr key={joueur._id}>
                            <td> {joueur.nom} {joueur.prenom} </td>
                            <td> {joueur.statut} </td>
                            <td> {joueur.postePref} </td>
                            <td> {nbSelection(joueur)} </td>
                            <td> </td>
                            <td> </td>
                            <td> </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </section>
    );
};

export default TableauStatistiques;
