import React, { useState, useEffect } from 'react';
import axios from 'axios';

const StatistiquesGloables = () => {

    const [matchs, setMatchs] = useState([]);
    const [nbMatchsGagnes, setNbMatchsGagnes] = useState(0);
    const [nbMatchsPerdus, setNbMatchsPerdus] = useState(0);
    const [nbMatchsNuls, setNbMatchsNuls] = useState(0);

    useEffect(() => {
        axios.get('http://localhost:5000/match')
            .then(respone => {
                setMatchs(respone.data.match);
                matchs.map(match => {
                    if (match.score > match.scoreAdverse) {
                        setNbMatchsGagnes(nbMatchsGagnes + 1);
                    } else if (match.score < match.scoreAdverse) {
                        setNbMatchsPerdus(nbMatchsPerdus + 1);
                    } else {
                        setNbMatchsNuls(nbMatchsNuls + 1);
                    }
                });
            })
            .catch(error => {
                console.log(error);
            });
    }, []);

    console.log("Nul : " + nbMatchsNuls);

    return (
        <section>
            <h1> Statistiques </h1>
            <h5> Nombre de matchs : {matchs.length} </h5>
            <h5> Pourcentage de matchs gagnes : {nbMatchsGagnes} </h5>
            <h5> Pourcentage de matchs perdus : {nbMatchsPerdus} </h5>
            <h5> Pourcentage de matchs nuls : {nbMatchsNuls} </h5>
        </section>
    );
};

export default StatistiquesGloables;
