import React, { useState } from 'react';

const FormAjoutMatch = ({ changerComponentAffiche }) => {
  const [dateMatch, setDateMatch] = useState('');
  const [heure, setHeure] = useState('');
  const [equipeAdverse, setEquipeAdverse] = useState('');
  const [lieu, setLieu] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    console.log('Date du match :', dateMatch);
    console.log('Heure du match :', heure);
    console.log('Equipe adverse :', equipeAdverse);
    console.log('Lieu du match :', lieu);

    setDateMatch('');
    setHeure('');
    setEquipeAdverse('');
    setLieu('');

    changerComponentAffiche('ListeDesMatchs');
  };

  return (
    <article>
      <header className="containerTitre">
        <h1>Ajouter un match</h1>
      </header>
      <div className="containerTabAndButtons">
        <form onSubmit={handleSubmit}>
          Date :{' '}
          <input
            type="date"
            name="dateMatch"
            value={dateMatch}
            onChange={(e) => setDateMatch(e.target.value)}
          />
          Heure :{' '}
          <input
            type="time"
            name="heure"
            value={heure}
            onChange={(e) => setHeure(e.target.value)}
          />
          Equipe adverse :{' '}
          <input
            type="text"
            name="equipeAdverse"
            value={equipeAdverse}
            onChange={(e) => setEquipeAdverse(e.target.value)}
          />
          Lieu :{' '}
          <input
            type="text"
            name="lieu"
            value={lieu}
            onChange={(e) => setLieu(e.target.value)}
          />
          <div>
            <button type="submit">Valider</button>
            <button onClick={() => changerComponentAffiche('ListeDesMatchs')}>
              Annuler
            </button>
          </div>
        </form>
      </div>
    </article>
  );
};

export default FormAjoutMatch;
