import React, { useState, useEffect } from 'react';
import axios from 'axios';

const SelectFeuilleMatch = ({changerComponentAffiche, idMatch}) => {
    const [joueurs, setJoueurs] = useState([]);
    const [erreur, setErreur] = useState('');
    const [joueursTitulaires, setJoueursTitulaires] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:5000/joueur/')
            .then(response => {
                setJoueurs(response.data.joueur);
            })
            .catch(error => {
                console.log(error);
            });
    }, []);

    const creerFeuilleDeMatch = (e) => {
        e.preventDefault();

        if (joueursTitulaires.length < 7) {
            setErreur('Veuillez sélectionner au moins 7 joueurs titulaires');
            return;
        }
        setErreur('');
    }

    const handleTitulaireChange = (joueurId, isTitulaire) => {
        if (isTitulaire) {
            setJoueursTitulaires([...joueursTitulaires, joueurId]);
        } else {
            setJoueursTitulaires(joueursTitulaires.filter(id => id !== joueurId));
        }
    }

    return (
        <article>
            <header className='containerTitre'>
                <h1>Sélection des joueurs de la feuille de match</h1>
            </header>
            <div className='containerTabAndButtons'>
                <table>
                    <thead>
                        <tr>
                            <th>Photo</th>
                            <th>Taille</th>
                            <th>Poids</th>
                            <th>Poste préféré</th>
                            <th>Titulaire ou remplaçant</th>
                            <th>Sélectionner</th>
                        </tr>
                    </thead>
                    <tbody>
                        {joueurs.map(joueur => (
                            <tr key={joueur._id}>
                                <td> {joueur.photo} </td>
                                <td> {joueur.taille} </td>
                                <td> {joueur.poids} </td>
                                <td> {joueur.postePref} </td>
                                <td>
                                    <select onChange={(e) => handleTitulaireChange(joueur._id, e.target.value === 'titulaire')}>
                                        <option value='titulaire'>Titulaire</option>
                                        <option value='remplacant'>Remplaçant</option>
                                    </select>
                                </td>
                                <td><input type='checkbox'/></td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                <div>
                    <p>7 Joueurs Titulaires requis pour valider</p>
                    <button onClick={creerFeuilleDeMatch}>Valider</button>
                    <button onClick={() => changerComponentAffiche('ListeDesMatchs')}>Annuler</button>
                    <p>{erreur}</p>
                </div>
            </div>
        </article>
    );
};

export default SelectFeuilleMatch;
