import React, { useState } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import AccueilConnecte from './pages/AccueilConnecte';
import AccueilNonConnecte from './pages/AccueilNonConnecte';
import AfficherJoueurs from './pages/AfficherJoueurs';
import Statistiques from './pages/Statistiques';
import AfficherMatchs from './pages/AfficherMatchs';
import PageConnexion from './pages/PageConnexion';
import { hasAuthenticated } from './services/Auth';
import Auth from './contexts/Auth';
import AuthenticatedRoute from './components/AuthenticatedRoute';

const App = () => {

  const [isAuthenticated, setIsAuthenticated] = useState(hasAuthenticated());

  return (
    <Auth.Provider value={({ isAuthenticated })} >
      <BrowserRouter>
        <Routes>
          <Route path='/' element={<AccueilNonConnecte />} />
          <Route path='/login' element={<PageConnexion />} />
          <Route path='*' element={<AccueilNonConnecte />} />
          <AuthenticatedRoute path='/home' element={<AccueilConnecte />} />
          <AuthenticatedRoute path='/joueurs' element={<AfficherJoueurs />} />
          <AuthenticatedRoute path='/matchs' element={<AfficherMatchs />} />
          <AuthenticatedRoute path='/statistiques' element={<Statistiques />} />
        </Routes>
      </BrowserRouter>
    </Auth.Provider>
  )
};

export default App;
