import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ConfirmSupprFeuilleMatch = ({ changerComponentAffiche, idMatch }) => {
    const [participations, setParticipations] = useState([]);
    const [joueurs, setJoueurs] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:5000/participation/match/' + idMatch)
            .then(response => {
                setParticipations(response.data.participation);
            })
            .catch(error => {
                console.log(error);
            });
    }, []);

    useEffect(() => {
        axios.get('http://localhost:5000/joueur/')
            .then(response => {
                setJoueurs(response.data.joueur);
            })
            .catch(error => {
                console.log(error);
            });
    }, []);
    
    return (
        <article>
            <header className='containerTitre'>
                <h1>Voulez vous vraiment supprimer cette feuille de match ?</h1>
            </header>
            <div className='containerTabAndButtons'>
                <table>
                    <thead>
                        <tr>
                            <th>Photo</th>
                            <th>Taille</th>
                            <th>Poids</th>
                            <th>Poste préféré</th>
                            <th>Titulaire ou remplaçant</th>
                            <th>Commentaire</th>
                            <th>Note</th>
                        </tr>
                    </thead>
                    <tbody>
                        {participations.map(participation => {
                            console.log(joueurs.find((joueur) => joueur._id === participation.idJoueur));
                            
                            const joueurCourant = joueurs.find((joueur) => joueur._id === participation.idJoueur);
                            const photo = joueurCourant.photo;
                            const taille = joueurCourant.taille;
                            const poids = joueurCourant.poids;
                            const postePref = joueurCourant.postePref;

                            return (
                                <tr key={participation.id}>
                                    <td> <img className='photo' src={require(`../../public/assets/images/joueurs/uploads/${photo}`)} alt={photo} /> </td>
                                    <td> {taille} </td>
                                    <td> {poids} </td>
                                    <td> {postePref} </td>
                                </tr>
                            )
                            
                        })}
                    </tbody>
                </table>
                <div>
                    <button>Oui</button>
                    <button onClick={() => changerComponentAffiche('ListeDesMatchs')}>Non</button>
                </div>
            </div>
        </article>
    );
};

export default ConfirmSupprFeuilleMatch;
