import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TableauStatistiques = () => {

    const [joueurs, setJoueurs] = useState([]);
    const [nbMatchs, setNbMatchs] = useState([]);
    const [nbSelections, setNbSelections] = useState({});
    const [isLoading, setIsLoading] = useState(true);
    const [dataLoaded, setDataLoaded] = useState(false);

    useEffect(() => {
        const fetchJoueurs = axios.get('http://localhost:5000/joueur/');
        const fetchNbMatchs = axios.get('http://localhost:5000/match');

        Promise.all([fetchJoueurs, fetchNbMatchs])
            .then(responses => {
                const reponseJoueur = responses[0];
                setJoueurs(reponseJoueur.data.joueur);
                const matchs = responses[1].data.match;

                const fetchSelections = joueurs.map(joueur =>
                    axios.get(`http://localhost:5000/participation/joueur/${joueur._id}`)
                        .then(response => {
                            response.data.participation.forEach(participation => {
                                setNbSelections(prevState => {
                                    const nbSelectionTitulaire = prevState[joueur._id]?.nbSelectionTitulaire || 0;
                                    const nbSelectionRemplacant = prevState[joueur._id]?.nbSelectionRemplacant || 0;

                                    if (participation.poste === "titulaire") {
                                        return {
                                            ...prevState,
                                            [joueur._id]: {
                                                nbSelectionTitulaire: nbSelectionTitulaire + 1,
                                                nbSelectionRemplacant
                                            }
                                        };
                                    } else {
                                        return {
                                            ...prevState,
                                            [joueur._id]: {
                                                nbSelectionTitulaire,
                                                nbSelectionRemplacant: nbSelectionRemplacant + 1
                                            }
                                        };
                                    }
                                });
                            });
                        })
                );

                Promise.all(fetchSelections).then(() => {
                    setIsLoading(false);
                    setDataLoaded(true);
                });
                setNbMatchs(matchs.length);
            })
            .catch(error => {
                console.log(error);
            });
    }, []);

    if (isLoading) {
        return <div>Loading...</div>;
    }

    if (!dataLoaded) {
        return <div>Data loading...</div>;
    }

    return (
        <section>
            <table>
                <thead>
                    <tr>
                        <th> Joueur </th>
                        <th> Statut </th>
                        <th> Poste préféré </th>
                        <th> Nombre total de sélection en tant que titulaire </th>
                        <th> Nombre total de sélection en tant que remplaçant </th>
                    </tr>
                </thead>
                <tbody>
                    {joueurs.map(joueur => (
                        <tr key={joueur._id}>
                            <td>{joueur.nom} {joueur.prenom}</td>
                            <td>{joueur.statut}</td>
                            <td>{joueur.poste_prefere}</td>
                            <td>{nbSelections[joueur._id]?.nbSelectionTitulaire || 0}</td>
                            <td>{nbSelections[joueur._id]?.nbSelectionRemplacant || 0}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <p>Nombre de matchs joués : {nbMatchs}</p>
        </section>
    );
};

export default TableauStatistiques;
