import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TableauStatistiques = () => {

    const [joueurs, setJoueurs] = useState([]);
    const [nbMatchs, setNbMatchs] = useState([]);
    const [nbSelections, setNbSelections] = useState({});
    const [donneesChargees, setDonneesChargees] = useState(false); // Ajout d'une variable d'état pour indiquer si les données ont été chargées

    useEffect(() => {
        axios.get('http://localhost:5000/joueur/')
            .then(reponseJoueur => {
                setJoueurs(reponseJoueur.data.joueur);
                joueurs.map(joueur => {
                    axios.get('http://localhost:5000/participation/joueur/' + joueur._id)
                        .then(response => {
                            response.data.participation.forEach(participation => {
                                setNbSelections(prevState => {
                                    const nbSelectionTitulaire = prevState[joueur._id]?.nbSelectionTitulaire || 0;
                                    const nbSelectionRemplacant = prevState[joueur._id]?.nbSelectionRemplacant || 0;

                                    if (participation.poste === "titulaire") {
                                        return {
                                            ...prevState,
                                            [joueur._id]: {
                                                nbSelectionTitulaire: nbSelectionTitulaire + 1,
                                                nbSelectionRemplacant
                                            }
                                        };
                                    } else {
                                        return {
                                            ...prevState,
                                            [joueur._id]: {
                                                nbSelectionTitulaire,
                                                nbSelectionRemplacant: nbSelectionRemplacant + 1
                                            }
                                        };
                                    }
                                })
                            })
                        })
                });
            })
            .catch(error => {
                console.log(error);
            });

        axios.get('http://localhost:5000/match')
            .then(respone => {
                setNbMatchs((respone.data.match).length);
            })
            .catch(error => {
                console.log(error);
            });
    }, []);

    useEffect(() => { // Ajout d'un effet pour mettre à jour l'état de la variable donneesChargees
        if (joueurs.length > 0 && Object.keys(nbSelections).length > 0 && nbMatchs !== null) {
            setDonneesChargees(true);
        }
    }, [joueurs, nbSelections, nbMatchs]);


}

export default TableauStatistiques;
