import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TableauStatistiques = () => {

    const [joueurs, setJoueurs] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:5000/joueur/')
            .then(response => {
                setJoueurs(response.data.joueur);
            })
            .catch(error => {
                console.log(error);
            });
    }, []);

    return (
        <section>
            <table>
                <thead>
                    <tr>
                        <th> Joueur </th>
                        <th> Statut </th>
                        <th> Poste préféré </th>
                        <th> Nombre total de sélection en tant que titulaire </th>
                        <th> Nombre total de sélection en tant que remplacant </th>
                        <th> Moyenne des evaluations </th>
                        <th> Pourcentage de matchs gagne </th>
                    </tr>
                </thead>
                <tbody>
                    {joueurs.map(joueur => (
                        <tr key={joueur._id}>
                            <td> <img className='photo' src={require(`../../public/assets/images/joueurs/uploads/${joueur.photo}`)} alt={joueur.photo} /> </td>
                            <td> {joueur.nom} </td>
                            <td> {joueur.prenom} </td>
                            <td> {joueur.numLicence} </td>
                            <td> {calculateAge(joueur.dateDeNaissance)} </td>
                            <td> {joueur.taille} </td>
                            <td> {joueur.poids} </td>
                            <td> {joueur.postePref} </td>
                            <td> {joueur.statut} </td>
                            <td> <a href="#" onClick={() => handleModifJoueurChange(true, { joueur })}> Modifier </a></td>
                            <td> <a href="#" onClick={() => handleSuppJoueurChange(true, { joueur })}> Supprimer </a></td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </section>
    );
};

export default TableauStatistiques;
