import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TableauStatistiques = () => {

    const [joueurs, setJoueurs] = useState([]);
    const [nbMatchs, setNbMatchs] = useState([]);
    const [statistiques, setStatistiques] = useState({
        nomJoueur: "oui",
        statutJoueur: null,
        postePrefJoueur: null,
        nbSelectionTitulaire: 0,
        nbSelectionRemplacant: 0,
        moyEval: 0,
        pourcentMatchsGagnes: 0

    });

    useEffect(() => {
        axios.get('http://localhost:5000/joueur/')
            .then(reponseJoueur => {
                setJoueurs(reponseJoueur.data.joueur);

                joueurs.map(joueur => {
                    axios.get('http://localhost:5000/participation/joueur/' + joueur._id)
                        .then(reponseParticipation => {
                            const poste = reponseParticipation.poste;
                            if (poste === "titulaire") {
                                setStatistiques({
                                    nomJoueur: joueur.nom,
                                    statutJoueur: joueur.statut,
                                    postePrefJoueur: joueur.postePref,
                                    nbSelectionTitulaire: 1
                                })
                            } else {
                                setStatistiques({
                                    nomJoueur: joueur.nom,
                                    statutJoueur: joueur.statut,
                                    postePrefJoueur: joueur.postePref,
                                    nbSelectionRemplacant: 1
                                })
                            }
                        })
                        .catch(error => {
                            console.log(error);
                        })
                })

            })
            .catch(error => {
                console.log(error);
            });
        axios.get('http://localhost:5000/match')
            .then(respone => {
                setNbMatchs((respone.data.match).length);
            })
            .catch(error => {
                console.log(error);
            });
    }, []);

    return (
        <section>
            <table>
                <thead>
                    <tr>
                        <th> Joueur </th>
                        <th> Statut </th>
                        <th> Poste préféré </th>
                        <th> Nombre total de sélection en tant que titulaire </th>
                        <th> Nombre total de sélection en tant que remplacant </th>
                        <th> Moyenne des evaluations </th>
                        <th> Pourcentage de matchs gagne </th>
                    </tr>
                </thead>
                <tbody>
                    {Object.keys(statistiques).forEach((joueur) => (
                        <tr key={joueur._id}>
                            <td> {joueur.nomJoueur} </td>
                            <td> {joueur.statut} </td>
                            <td> {joueur.postePref} </td>
                            <td>  </td>
                            <td> </td>
                            <td> </td>
                            <td> </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </section>
    );
};

export default TableauStatistiques;
