import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ModifFeuilleMatch = ({ changerComponentAffiche, idMatch }) => {
    const [participations, setParticipations] = useState([]);
    const [joueurs, setJoueurs] = useState([]);
    const [commentaires, setCommentaires] = useState([]);
    const [valeurs, setValeurs] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:5000/participation/match/' + idMatch)
            .then(response => {
                setParticipations(response.data.participation);
            })
            .catch(error => {
                console.log(error);
            });
    }, []);

    useEffect(() => {
        axios.get('http://localhost:5000/joueur/')
            .then(response => {
                setJoueurs(response.data.joueur);
            })
            .catch(error => {
                console.log(error);
            });
    }, []);

    const handleChange = (participation, index, e) => {
        let newValue = e.target.value;
        if (e.target.value === '') {
            newValue = 0;
        }
        const updatedParticipations = [...participations];
        updatedParticipations[index].note = newValue;
        setParticipations(updatedParticipations);
        const updatedValeurs = [...valeurs];
        updatedValeurs[index] = newValue;
        setValeurs(updatedValeurs);
    };

    const handleCommentaireChange = (participation, index, e) => {
        const updatedParticipations = [...participations];
        updatedParticipations[index].commentaire = e.target.value;
        setParticipations(updatedParticipations);
        const updatedCommentaires = [...commentaires];
        updatedCommentaires[index] = e.target.value;
        setCommentaires(updatedCommentaires);
    };

    const modificationPage = () => {
        // Utilisez les tableaux commentaires et valeurs pour les données modifiées
        console.log(commentaires);
        console.log(valeurs);
    }

    return (
        <article>
            <header className='containerTitre'>
                <h1>Modification de la feuille de match</h1>
            </header>
            <div className='containerTabAndButtons'>
                <table>
                    <thead>
                        <tr>
                            <th>Photo</th>
                            <th>Taille</th>
                            <th>Poids</th>
                            <th>Poste préféré</th>
                            <th>Titulaire ou remplaçant</th>
                            <th>Commentaire</th>
                            <th>Note</th>
                        </tr>
                    </thead>
                    <tbody>
            {participations.map((participation, index) => {
                const joueurCourant = joueurs.find((joueur) => joueur._id === participation.idJoueur);
                const valeur = participation.note;

                setCommentaires(participation.commentaire);

                return (
                    <tr key={participation.id}>
                        <td> {joueurCourant ? <img className='photo' src={require(`../../public/assets/images/joueurs/uploads/${joueurCourant.photo}`)} alt={joueurCourant.photo} /> : null} </td>
                        <td> {joueurCourant ? joueurCourant.taille : null} </td>
                        <td> {joueurCourant ? joueurCourant.poids : null} </td>
                        <td> {joueurCourant ? joueurCourant.postePref : null} </td>
                        <td> {participation.poste} </td>
                        <td> <textarea onChange={(e) => setCommentaires(e.target.value)}>{commentaires}</textarea> </td>
                        <td> <input type='number' min='0' max='5' value={valeur} onChange={(e) => handleChange(participation, setParticipations, index, e)}></input> </td>
                    </tr>
                )
            })}
        </tbody>

                </table>
                <div>
                    <button onClick={modificationPage}>Valider</button>
                    <button onClick={() => changerComponentAffiche('ListeDesMatchs')}>Annuler</button>
                </div>
            </div>
        </article>
    );
};

export default ModifFeuilleMatch;
