import React, { useState } from 'react';
import FormAjouterJoueur from './FormAjouterJoueur';
import CreateJoueurTableau from './CreateJoueurTableau';

const TableauJoueurs = () => {

    const [showForm, setShowForm] = useState(false);

    return (
        <article className='centrerTableau'>
            <header>
                <h1> Liste des joueurs </h1>
                <button className='ajouterJoueur' onClick={() => setShowForm(true)}> Ajouter un joueur </button>
            </header>
            {showForm && <FormAjouterJoueur />}
            <CreateJoueurTableau />
        </article>
    );
};

export default TableauJoueurs;
